import React, { Component } from 'react';
import './Home.scss';
import MenuItem from './MenuItem';
import './MenuItem.scss';

const categories = [
  { title: 'HATS', subtitle: 'SHOP NOW' },
  { title: 'JACKETS', subtitle: 'SHOP NOW' },
  { title: 'SNEAKERS', subtitle: 'SHOP NOW' },
  { title: 'MENS', subtitle: 'SHOP NOW' },
  { title: 'WOMENS', subtitle: 'SHOP NOW' }
];

class Home extends Component {
  renderMenu() {
    return categories.forEach(({ title, subtitle }) => (
      <MenuItem title={title} subtitle={subtitle} />
    ));
  }

  render() {
    return (
      <div className="homepage">
        <div className="directory-menu">{this.renderMenu}</div>
      </div>
    );
  }
}

export default Home;
