import { takeEvery } from 'redux-saga/effects';
import {
  SET_CURRENT_USER,
  TOGGLE_CART_VISIBILITY,
  ADD_ITEM,
  CLEAR_ITEM_FROM_CART,
  REMOVE_ITEM,
  FETCH_COLLECTIONS_START,
  FETCH_COLLECTIONS_SUCCESS,
  FETCH_COLLECTIONS_FAILURE
} from '../actions/types';

export function* fetchCollectionsAsync() {
  yield console.log('fired');

  const collectionRef = firestore.collection('collections');
  dispatch({ type: FETCH_COLLECTIONS_START });

  collectionRef
    .get()
    .then(snapshot => {
      const collectionsMap = convertCollectionsSnapshotToMap(snapshot);
      dispatch({ type: FETCH_COLLECTIONS_SUCCESS, payload: collectionsMap });
    })
    .catch(err => dispatch({ type: FETCH_COLLECTIONS_FAILURE, payload: err }));
}

export function* fetchCollectionsStart() {
  yield takeEvery(FETCH_COLLECTIONS_START, fetchCollectionsAsync);
}
