import React, { useEffect } from 'react';
import { BrowserRouter, Route, Switch, Redirect } from 'react-router-dom';
import { Elements } from '@stripe/react-stripe-js';
import { loadStripe } from '@stripe/stripe-js';

import './App.css';
import AuthenticationPage from './pages/AuthenticationPage';
import HomePage from './pages/HomePage';
import ShopPageContainer from '../containers/ShopPageContainer';
import HeaderContainer from '../containers/HeaderContainer';
import CheckoutPageContainer from '../containers/CheckoutPageContainer';
import CheckoutFormPage from './pages/CheckoutFormPage';

const stripePromise = loadStripe(
  'pk_test_51Hxnc3DhdNsUybZdKLR0cyelTOGTMYWf8G5Umw78zVC895bPkuhJUG6TpnSSAwWGabcP2SNBlmGuMJx2WAGFinu800F2giYVCs'
);

const App = ({ checkUserSession, currentUser }) => {
  useEffect(() => {
    checkUserSession();
  }, [checkUserSession]);

  return (
    <BrowserRouter>
      <HeaderContainer />
      <div>
        <Switch>
          <Elements stripe={stripePromise}>
            <CheckoutForm />
          </Elements>
          <Route path="/" exact component={HomePage} />
          <Route path="/shop" component={ShopPageContainer} />
          <Route path="/checkoutForm" component={CheckoutFormPage} />
          <Route
            path="/authentication"
            exact
            render={() =>
              currentUser ? <Redirect to="/" /> : <AuthenticationPage />
            }
          />
          <Route path="/checkout" exact component={CheckoutPageContainer} />
        </Switch>
      </div>
    </BrowserRouter>
  );
};

export default App;
